import { useEffect, useState } from 'react';
import { AiOutlineArrowLeft, AiOutlineArrowRight } from 'react-icons/ai'
import './App.scss';

export default function App() {
  const [previous, setPrevious] = useState([]);
  const [undo, setUndo] = useState([]);

  const [circles, setCircles] = useState([]);
  const [mousePos, setMousePos] = useState({});

  useEffect(() => {
    const handleMouseMove = (event) => {
      setMousePos({ x: Math.ceil(event.clientX), y: Math.ceil(event.clientY) });
      const circle = {
        x: Math.ceil(event.clientX), 
        y: Math.ceil(event.clientY),
      }
      setCircles(prev => [...prev, circle])
    };

    document.getElementById('app').addEventListener('pointerdown', handleMouseMove);
    //window.addEventListener('pointerdown', handleMouseMove);

    return () => {
     // window.removeEventListener('pointerdown',handleMouseMove);
     document.getElementById('app').removeEventListener('pointerdown', handleMouseMove);
    };
  }, []);


  return (
    <div className="app" id='app'>
      <div style={{position: 'absolute', right: '600px'}}>
        The mouse is at position{' '}
        <b>
          ({mousePos.x}, {mousePos.y})
        </b>
      </div>
      <div className='navbar'>
        <button onClick={(e) => {e.stopPropagation()}}><AiOutlineArrowLeft/></button>
        <button onClick={(e) => {e.stopPropagation()}}><AiOutlineArrowRight/></button>
      </div>

      {
        circles.map(((circle, idx) => {
          return (
            <div className='circle' key={idx} style={{position: 'absolute', left: circle.x - 25, top: circle.y - 25}}>
              {idx}
            </div>
          )
        }))
      }
    </div>
  )
}
